// Migrate popups configs.
$installer->run("UPDATE `{$this->getTable('newsletterpopup_popups')}` SET `template_id` = 20");

// Migrate rules.
$popups = Mage::getModel('newsletterpopup/popup')->getCollection();
foreach($popups as $popup) {
	$rule = Mage::helper('newsletterpopup/adminhtml')->getDefaultRule();

	$devices = explode(',', $popup['devices']);
	if(!in_array('all', $devices)) {
		$rule['conditions'][] = array(
			'type' => 'newsletterpopup/popup_condition_general',
			'attribute' => 'current_device',
			'operator' => '{}',
			'value' => $devices,
			'is_value_processed' => false,
		);
	}

	$area = explode(',', $popup['show_on']);
	if(!in_array('all', $area)) {
		$rule['conditions'][] = array(
			'type' => 'newsletterpopup/popup_condition_general',
			'attribute' => 'current_page_type',
			'operator' => '{}',
			'value' => $area,
			'is_value_processed' => false,
		);
	}

	$groups = explode(',', $popup['customers_groups']);
	$rule['conditions'][] = array(
		'type' => 'newsletterpopup/popup_condition_customer',
		'attribute' => 'group_id',
		'operator' => '{}',
		'value' => $groups,
		'is_value_processed' => false,
	);

	$installer->run("UPDATE `{$this->getTable('newsletterpopup_popups')}` SET `conditions_serialized` = '". serialize($rule) ."' WHERE `entity_id` = '{$popup['entity_id']}'");
}


